import React from 'react';

const Card = ({name, email, id}) => {
	return (
		<div className='tc bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5'>
		<img alt='robots' src={`https://robohash.org/set_set3/${id}?80x80`} />
		<div>
			<h2>{name}</h2>
			<p>{email}</p>
		 </div>
		</div>
		);
}

export default Card;
